import React, { useEffect, useState } from "react";
import Footer from "./Footer";
import { Link, useHistory } from "react-router-dom/cjs/react-router-dom.min";
import axios from "axios";

export default function Addsalary() {
  const [users, setUsers] = useState([]);
 const [formData,setFormData]=useState({
      basic_salary:"",
      allowance:"",
 })
 const navigate=useHistory()
 useEffect(() => {
  const fetchUsers = async () => {
    try {
      const res = await axios.get("http://localhost:3001/staffList");
      setUsers(res.data);
    } catch (err) {
      console.log("Error fetching details", err);
    }
  };

  fetchUsers();
}, []);
 const handleChange=(event)=>{
  let oldData={...formData}
  let inputValue=event.target.value;
  let inputName=event.target.name;
  oldData[inputName] = inputValue;
  setFormData(oldData)
};
function calculate(){
  let basicSalary=document.getElementById("basicSalary").value;
  let alloWance= document.getElementById("alloWance").value;
  var totalSalary=(basicSalary+alloWance)
  console.log(totalSalary);
  // id="total"
 var  salary=document.getElementById("total")
 salary.innerHTML=totalSalary;


  
}
const handleSubmit=async(e)=>{
  e.preventDefault();
  if(validateForm()){

    try{
      const response=await axios.post("http://localhost:3001/addSalary",formData);
      if(response.status===201){
        alert("Salary Added Succesfully!!!")
        navigate.push('/manageSalary');
      }
      else
      {
        alert("Error adding salary details")
      }
    } catch(error){
          // Handle error response
          if (error.response && error.response.status === 400) {
            alert(error.response.data.error); // Display the error message to the user
            alert("Error adding staffs for duplicate value. Try again with updated Data");
          } else {
            console.error("Error adding staff:", error);
          }
    }
  }
//Handel form submission

let currentUserFormData={
  basic_salary:formData.basic_salary,
  allowance:formData.allowance
};

let oldUserdata=[...users,currentUserFormData];
setUsers(oldUserdata)
console.log(formData)

//To empty the input fields after adding/getting the data
setFormData ({
basic_salary:"",
allowance:"",

})
}



const validateForm=()=>{
  const {
    basic_salary,
    allowance,
  }=formData;

const errors={};
if(!basic_salary.trim()){
  errors.basic_salary="Basic Salary is required";
  document.getElementById("basic_salary").focus();
} 
else if(!allowance.trim()){
  errors.allowance="Allowance is required";
  document.getElementById("allowance").focus();
}


if (Object.keys(errors).length === 0) {
  return true;
} else {
  alert(Object.values(errors)[0]);
  return false;
}




};

  function calc() {
    let n1 = parseInt(document.getElementById("basic_salary").value);
    let n2 = parseInt(document.getElementById("allowance").value);
    let sum = n1 + n2;
    document.getElementById("total").value = sum;
  }



  return (
    <>
      <nav
        className="navbar navbar-expand-lg"
        style={{ backgroundColor: "rgb(0 77 255 / 65%)" }}
      >
        {/* Navbar content */}
      </nav>
      <div className="container my-2">
        <h2>Salary</h2>
      </div>
      <div className="container-fluid pt-3" style={{ padding: "100px" }}>
        <div className="row d-flex justify-content-evenly">
          <div style={{ background: "white", padding: "21px", borderTop: "5px solid #004dffe8", borderRadius: "5px" }}>
            <h5 style={{ fontSize: "20px" }} className="px-2">
              Add Salary
            </h5>
            <hr />
            <div className="container">
              <div className="row">
                <div className="col-md-4">
                  <div className="mb-3">
                    <b className="">Department Name</b>
                    <select
                      className="form-control"
                      style={{ border: "1px solid" }}
                    >
                      <option disabled defaultValue={"--Department Name--"}>
                        --Department Name--
                      </option>
                      <option>Backend developement</option>
                      <option>Designing</option>
                      <option>Front-end developement</option>
                      <option>Marketing</option>
                      <option>Finance</option>
                    </select>
                  </div>
                </div>
              </div>
            </div>
            <div className="container m-3">
              <div className="row p-3">
                {users.length > 0 ? (
                  <table>
                    <thead>
                      <tr>
                        <th className="col-md-3 text-center bg- p-1 px-2 tablestyle">
                          Staff
                        </th>
                        <th className="col-md-3 text-center bg- p-1 px-2 tablestyle">
                          Basic Salary($)
                        </th>
                        <th className="col-md-3 text-center bg- p-1 px-2 tablestyle">
                          Allowance($)
                        </th>
                        <th className="col-md-3 text-center bg- p-1 px-2 tablestyle">
                          Total($)
                        </th>
                      </tr>
                    </thead>
                    <tbody>
                      {users.map((user, index) => (
                        <tr key={index}>
                          <td className="col-md-3 bg- p-1 px-2 tablestyle">
                            {user.user_name}
                          </td>
                          <td className="col-md-3 bg- p-1 px-2 tablestyle">
                            <input
                              type="text"
                              
                              id="basic_salary"
                              name="basic_salary"
                              placeholder="Enter the basic amount"
                              value={formData.basic_salary}
                              onChange={handleChange}
                              onSubmit={handleSubmit}

                              style={{ width: "100%" }}
                            />
                          </td>
                          <td className="col-md-3 bg- p-1 px-2 tablestyle">
                            <input
                              type="text"
                              id="allowance"
                              name="allowance"
                              placeholder="Enter the basic amount"
                              value={formData.allowance}
                              onChange={handleChange}
                              onSubmit={handleSubmit}
                              style={{ width: "100%" }}
                            />
                            {/* {error.allowance && <div className="text-danger">{errors.allowance}</div>} */}
                          </td>
                          <td className="col-md-3 bg- p-1 px-2 tablestyle">
                            <input
                              type="text"
                              id="total"
                              onChange={calculate}
                              
                              readOnly
                              style={{ width: "100%" }}
                            />
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                ) : (
                  <div>No data available</div>
                )}
              </div>
            </div>
            <div className="container">
              <button
                type="submit"
                className="btn btn-success float-end"
                onClick={handleSubmit}
                onSubmit={handleSubmit}
              >
                Submit
              </button>
            </div>
          </div>
        </div>
      </div>
      <Footer footerstyle="fixed-bottom" />
    </>
  );
}
