import axios from "axios";
import React, { useEffect, useState } from "react";
import { useHistory, useParams } from "react-router-dom";

export default function EditDepartment({ initialData }) {
  const { id } = useParams();
  const navigate = useHistory();

  // Define formData state
  const [formData, setFormData] = useState({
    deptid: "",
    deptname: ""
  });

  // Update form data when initialData prop changes
  useEffect(() => {
    if (initialData) {
      setFormData({
        deptid: initialData.deptid,
        deptname: initialData.deptname
      });
    }
  }, [initialData]);

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    // Implement form submission logic
  };

  return (
    <div className="modal fade text-start" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
      <div className="modal-dialog">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title" id="exampleModalLabel">Edit Department</h5>
            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
          </div>
          <form onSubmit={handleSubmit}>
            <div className="modal-body">
              <div className="mb-3">
                <label htmlFor="departmentName" className="form-label" style={{ fontWeight: "500" }}>Department Name</label>
                <input type="text" className="form-control" id="departmentName" name="deptname" placeholder="Department Name" value={formData.deptname} onChange={(e) => setFormData({ ...formData, deptname: e.target.value })} />
              </div>
              <div className="mb-3">
                <label htmlFor="departmentId" className="form-label" style={{ fontWeight: "500" }}>Department Id</label>
                <input type="text" className="form-control" id="departmentId" name="deptid" placeholder="Department Id" value={formData.deptid} onChange={(e) => setFormData({ ...formData, deptid: e.target.value })} />
              </div>
            </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
              <button type="submit" className="btn btn-primary">Update Department</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
